hookFunctions.code:
const files = event.detail.files[0];

self.document.attachedDocuments()[self.documentsListMap[data.id]].fileName(files.name);

const formData = new FormData();

formData.append("file", files);
formData.append("transactionType", "MO");
formData.append("moduleIdentifier", "CREDIT_FACILITY");
formData.append("fileCount", 1);

contentspostCall(formData).then(function (response) {
    if (response.contentDTOList.length && response.contentDTOList[0] && response.contentDTOList[0].contentId) {
        self.document.attachedDocuments()[self.documentsListMap[data.id]].contentId.value(response.contentDTOList[0].contentId.value);
    }
});

self.payload.collateralCode = self.collateralId();
self.payload.document = ko.mapping.toJS(self.document);
self.payload.collateralValue.amount = self.collateralAmount();
self.payload.collateralValue.currency = self.collateralAmountCurrency();

setExtraParams({
   collateralId: self.collateralId,
                    collateralIdIndex: self.collateralIdIndex,
                    collateralAmount: self.collateralAmount,
                    collateralAmountCurrency: self.collateralAmountCurrency,
                    utilizedAmount: self.utilizedAmount,
                    utilizedAmountCurrency: self.utilizedAmountCurrency,
                    availableAmount: self.availableAmount,
                    availableAmountCurrency: self.availableAmountCurrency,
                    revisionDate: self.revisionDate,
                    collateralDescription: self.collateralDescription,
                    openModal: self.openModal,
                    dataLoaded: self.dataLoaded,
                    collateralList: self.collateralList,
                    reasonForModification: self.reasonForModification,
                    remarks: self.remarks,
                    document: self.document,
                    liabilityId: self.liabilityId
});

                if (!params.baseModel.showComponentValidationErrors(document.getElementById("collateralRevaluationTracker"))) {
                    return false;
                }

                return true;

