data.css:
.confirm-screen {
	position: relative;
	padding: 0;
	@include mq(small) {
		padding: 0.5rem 0;
	}

	&__outer-div {
		list-style-type: none;
		@include single-padding(left,0);
		display: flex;
		flex-wrap: wrap;

		&__inner-div {
			padding: 0.6rem;
			@include float(left);
			min-width: 8.1rem;
			text-align: center;
			justify-content: space-between;

			img {
				height: 3.8rem;
			}
		}
	}

	&-messageContainer {
		&__message {
			.row{
				padding-bottom:0.5rem;
			}
		}
	}

}

