data.css:
$submitted-background-color: #0A8BE6;
$draft-background-color:  #ffd70b;
$inProgress-background-color: var(--base-color-warning);
$box-background-color: #D3D3D3;
$panel-hover-color:#e6faff;

.application-tracker-header{
            padding-bottom:0.5rem;
            font-size: var(--form-header-font-size);
            .img-customizations{
                height: 2.5rem;
                @include single-padding(left, 1rem);
            }
            .header-customizations{
                @include single-margin(top, 0.7rem);
            }
}
.template-search{
    @include single-margin(top, 0.5rem);
}
.filter-margin{
    @include mq(large){
        @include single-padding(left, 4.5rem);
        }
        @include mq(small){
            @include single-margin(top, 1rem);
            }
            @include mq(medium){
                @include single-margin(left, 0rem);
                }
        margin-top: .6rem;
    
}
.demo-panel-customizations {
    @include mq(large){
        width: 22rem;
      }
    cursor: pointer;
    @include float(left);
    padding: 0.6rem 0.6rem 0.6rem 0.6rem; 
    box-shadow: 0 0 5px $box-background-color;
    border-radius: .3rem;
}
.submit{
    @include single-border(left, 0.3rem solid $submitted-background-color);
}
.in-progress {
    @include single-border(left, 0.3rem solid $inProgress-background-color);
}
.draft {
    @include single-border(left, 0.3rem solid $draft-background-color);
}
.approved{
    @include single-border(left, 0.3rem solid  var(--base-color-success));
}

.rejected{
    @include single-border(left, 0.3rem solid var(--base-color-danger));

}
.panel-child3-customizations {
    color: rgba(44, 50, 81, 0.8);
    font-size: 0.7rem;
}
.customize-status{ 
    @include single-padding(left, 0.5rem);
    @include single-margin(left, 0.5rem);
    @include single-border(left, 0.05rem solid  var(--base-background-tertiary));}

